        "format-alt": "{time} {icon}",
        "format-icons": ["ó°º", "ó°»", "ó°¼", "ó°½", "ó°¾", "ó°¿", "ó°‚€", "ó°‚", "ó°‚‚", "ó°¹"]
    },
    
    "network": {
        "format-wifi": "{essid} ({signalStrength}%) ó°¤¨",
