#battery,
#network,
#pulseaudio,
#custom-media,
#custom-power,
#tray {
    background: $surface0_rgba;
    padding: 6px 14px;
    margin: 5px 2px 5px 0px;
    border-radius: 10px;
    color: $text;
    border: 1px solid ${accent}30;
}

#battery.charging {
    color: #a6e3a1;
    background: #a6e3a120;
    border-color: #a6e3a140;
}

#battery.warning:not(.charging) {
