}

#custom-power:hover {
    background: #f38ba830;
    transform: scale(1.05);
}

#custom-media {
    background: ${accent}15;
    color: $accent;
    border-color: ${accent}40;
    min-width: 100px;
}

#tray {
    margin: 5px 10px 5px 0px;
}

#tray > .passive {
    -gtk-icon-effect: dim;
}

#tray > .needs-attention {
    -gtk-icon-effect: highlight;
    background-color: ${accent}30;
}

tooltip {
    background: $bg;
    border: 2px solid $accent;
